// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/utils/structs/Checkpoints.sol";
import {IVanaEpoch} from "../../vanaEpoch/interfaces/IVanaEpoch.sol";
import {ITreasury} from "../../treasury/interfaces/ITreasury.sol";

interface IDLPRegistry {
    enum DlpStatus {
        None,
        Registered,
        Eligible,
        Deregistered
    }

    struct Dlp {
        uint256 id;
        address dlpAddress;
        address ownerAddress;
        address tokenAddress;
        address payable treasuryAddress;
        string name;
        string iconUrl;
        string website;
        string metadata;
        uint256 registrationBlockNumber;
        uint256 depositAmount;
        DlpStatus status;
        bool isVerified;
    }

    // View functions for contract state and configuration
    function version() external pure returns (uint256);
    function vanaEpoch() external view returns (IVanaEpoch);
    function treasury() external view returns (ITreasury);
    function eligibleDlpsListValues() external view returns (uint256[] memory);
    function eligibleDlpsListCount() external view returns (uint256);
    function eligibleDlpsListAt(uint256 index) external view returns (uint256);

    function dlpRegistrationDepositAmount() external view returns (uint256);
    function eligibleDlpsLimit() external view returns (uint256);
    function dlpsCount() external view returns (uint256);

    struct DlpInfo {
        uint256 id;
        address dlpAddress;
        address ownerAddress;
        address tokenAddress;
        address treasuryAddress;
        string name;
        string iconUrl;
        string website;
        string metadata;
        uint256 registrationBlockNumber;
        uint256 depositAmount;
        DlpStatus status;
        bool isVerified;
    }
    function dlps(uint256 dlpId) external view returns (DlpInfo memory);
    function dlpsByAddress(address dlpAddress) external view returns (DlpInfo memory);
    function dlpIds(address dlpAddress) external view returns (uint256);
    function dlpNameToId(string calldata dlpName) external view returns (uint256);
    function dlpsByName(string calldata dlpName) external view returns (DlpInfo memory);


    // Admin functions
    function pause() external;
    function unpause() external;
    function updateTreasury(address newTreasuryAddress) external;
    function updateVanaEpoch(address newVanaEpochAddress) external;
    function updateDlpRegistrationDepositAmount(uint256 newDlpRegistrationDepositAmount) external;

    struct DlpRegistration {
        address dlpAddress;
        address ownerAddress;
        address payable treasuryAddress;
        string name;
        string iconUrl;
        string website;
        string metadata;
    }

    // DLP lifecycle management
    function registerDlp(DlpRegistration calldata registrationInfo) external payable;
    function updateDlpVerification(uint256 dlpId, bool isVerified) external;
    function updateDlp(uint256 dlpId, DlpRegistration calldata dlpUpdateInfo) external;
    function deregisterDlp(uint256 dlpId) external;
    function updateDlpToken(uint256 dlpId, address tokenAddress) external;
}

contracts/swapHelper/interfaces/ISwapHel.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "@uniswap/v3-periphery/contracts/interfaces/IQuoterV2.sol";
import "./IWVANA.sol";

interface ISwapHelper {
    function WVANA() external view returns (IWVANA);

    function uniswapV3Router() external view returns (address);

    function uniswapV3Quoter() external view returns (IQuoterV2);

    function updateWVANA(IWVANA newWVANA) external;

    function updateUniswapV3Router(address newUniswapV3Router) external;

    function updateUniswapV3Quoter(IQuoterV2 newUniswapV3Quoter) external;

    struct ExactInputSingleParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        address recipient;
        uint256 amountIn;
        uint256 amountOutMinimum;
    }

    /// @notice Swaps `amountIn` of `tokenIn` for as much as possible of `tokenOut`. Similar to Uniswap V3's `exactInputSingle`.
    /// @param params The parameters for the swap.
    /// @return amountOut The amount of tokenOut received from the swap.
    function exactInputSingle(ExactInputSingleParams calldata params) external payable returns (uint256 amountOut);

    struct QuoteExactInputSingleParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        uint256 amountIn;
    }

    /// @notice Returns the `amountOut` received for a given exact `amountIn` for a swap of a single pool.
    /// @param params The parameters for the quote.
    /// @return amountOut The amount of tokenOut received from the swap.
    function quoteExactInputSingle(QuoteExactInputSingleParams calldata params) external returns (uint256 amountOut);

    struct SlippageSwapParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        address recipient;
        uint256 amountIn;
        uint256 maximumSlippagePercentage;
    }

    /// @notice Swaps as much as possible of `amountIn` while maintaining a maximum price slippage.
    /// @param params The parameters for the swap.
    /// @return amountInUsed The amount of tokenIn used for the swap.
    /// @return amountOut The amount of tokenOut received from the swap.
    function slippageExactInputSingle(
        SlippageSwapParams calldata params
    ) external payable returns (uint256 amountInUsed, uint256 amountOut);

    struct QuoteSlippageExactInputSingleParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        uint256 amountIn;
        uint256 maximumSlippagePercentage;
    }

    /// @notice Returns the as much as possible `amountInUsed` and `amountOut` while maintaining a maximum price slippage.
    /// @param params The parameters for the quote.
    /// @return amountInUsed The amount of tokenIn used for the swap.
    /// @return amountOut The amount of tokenOut received from the swap.
    function quoteSlippageExactInputSingle(
        QuoteSlippageExactInputSingleParams calldata params
    ) external returns (uint256 amountInUsed, uint256 amountOut);
}

