pragma solidity 0.8.28;

import {IDLPRegistry} from "../../dlpRegistry/interfaces/IDLPRegistry.sol";
import {IVanaEpoch} from "../../vanaEpoch/interfaces/IVanaEpoch.sol";

interface IDLPPerformance {
    struct EpochDlpPerformance {
        uint256 totalScore; //normalized score; sum of all scores in an epoch must be 1e18
        uint256 tradingVolume;
        uint256 uniqueContributors;
        uint256 dataAccessFees;
    }

    struct EpochPerformance {
        mapping(uint256 dlpId => EpochDlpPerformance epochDlpPreformance) epochDlpPerformances;
    }

    struct EpochDlpPerformanceInfo {
        uint256 totalScore;
        uint256 tradingVolume;
        uint256 uniqueContributors;
        uint256 dataAccessFees;
    }

    function version() external pure returns (uint256);
    function dlpRegistry() external view returns (IDLPRegistry);
    function vanaEpoch() external view returns (IVanaEpoch);
    function epochDlpPerformances(uint256 epochId, uint256 dlpId) external view returns (EpochDlpPerformanceInfo memory);

    function pause() external;
    function unpause() external;
    function updateDlpRegistry(address dlpRegistryAddress) external;
    function updateVanaEpoch(address vanaEpochAddress) external;

    struct EpochDlpPerformanceInput {
        uint256 dlpId;
        uint256 totalScore;
        uint256 tradingVolume;
        uint256 uniqueContributors;
        uint256 dataAccessFees;
    }
    function saveEpochPerformances(
        uint256 epochId,
        EpochDlpPerformanceInput[] calldata epochDlpPerformances,
        bool finalized
    ) external;
}
","keccak256":"0x71df6835a19a10e9c5063ad653cf7cd0da6cbf08ae66814998f8ded134648a61","license":"MIT"},"contracts/dlpRegistry/interfaces/IDLPRegistry.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/utils/structs/Checkpoints.sol";
import {IVanaEpoch} from "../../vanaEpoch/interfaces/IVanaEpoch.sol";
import {ITreasury} from "../../treasury/interfaces/ITreasury.sol";

interface IDLPRegistry {
    enum DlpStatus {
        None,
        Registered,
        Eligible,
        Deregistered
    }

    struct Dlp {
        uint256 id;
        address dlpAddress;
        address ownerAddress;
        address tokenAddress;
        address payable treasuryAddress;
        string name;
        string iconUrl;
        string website;
        string metadata;
        uint256 registrationBlockNumber;
        uint256 depositAmount;
        DlpStatus status;
        uint256 lpTokenId;
        bool isVerified;
    }

    // View functions for contract state and configuration
    function version() external pure returns (uint256);
    function vanaEpoch() external view returns (IVanaEpoch);
    function treasury() external view returns (ITreasury);
    function eligibleDlpsListValues() external view returns (uint256[] memory);
    function eligibleDlpsListCount() external view returns (uint256);
    function eligibleDlpsListAt(uint256 index) external view returns (uint256);

    function dlpRegistrationDepositAmount() external view returns (uint256);
    function eligibleDlpsLimit() external view returns (uint256);
    function dlpsCount() external view returns (uint256);

    struct DlpInfo {
        uint256 id;
        address dlpAddress;
        address ownerAddress;
        address tokenAddress;
        address treasuryAddress;
        string name;
        string iconUrl;
        string website;
        string metadata;
        uint256 registrationBlockNumber;
        uint256 depositAmount;
        DlpStatus status;
        uint256 lpTokenId;
        bool isVerified;
    }
    function dlps(uint256 dlpId) external view returns (DlpInfo memory);
    function dlpsByAddress(address dlpAddress) external view returns (DlpInfo memory);
    function dlpIds(address dlpAddress) external view returns (uint256);
    function dlpNameToId(string calldata dlpName) external view returns (uint256);
    function dlpsByName(string calldata dlpName) external view returns (DlpInfo memory);


    // Admin functions
    function pause() external;
    function unpause() external;
    function updateTreasury(address newTreasuryAddress) external;
    function updateVanaEpoch(address newVanaEpochAddress) external;
    function updateDlpRegistrationDepositAmount(uint256 newDlpRegistrationDepositAmount) external;

    struct DlpRegistration {
        address dlpAddress;
        address ownerAddress;
        address payable treasuryAddress;
        string name;
        string iconUrl;
        string website;
        string metadata;
    }

    // DLP lifecycle management
    function registerDlp(DlpRegistration calldata registrationInfo) external payable;
    function updateDlpVerification(uint256 dlpId, bool isVerified) external;
    function updateDlp(uint256 dlpId, DlpRegistration calldata dlpUpdateInfo) external;
    function deregisterDlp(uint256 dlpId) external;
    function updateDlpToken(uint256 dlpId, address tokenAddress) external;
    function updateDlpLpTokenId(uint256 dlpId, uint256 lpTokenId) external;
}
","keccak256":"0x9ebdd63d440b47d54347da1341d6981a33053e74bec423731c274f341164346a","license":"MIT"},"contracts/treasury/interfaces/ITreasury.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

interface ITreasury {
    /// @notice Pauses the contract
    function pause() external;

    /// @notice Unpauses the contract
    function unpause() external;

    function custodian() external view returns (address);

    function updateCustodian(address custodian) external;

    /// @notice Transfers a token to the given address
    /// @param to The address to transfer the token to
    /// @param token The token to transfer
    /// @param amount The amount of tokens to transfer
    function transfer(address to, address token, uint256 amount) external;
}
","keccak256":"0x16c64709cf461adf786d52b5fddb0a1d4d6ae8d31d9b4458c3c49a4ede798073","license":"MIT"},"contracts/vanaEpoch/VanaEpochImplementation.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "./interfaces/VanaEpochStorageV1.sol";

import "hardhat/console.sol";

contract VanaEpochImplementation is
    UUPSUpgradeable,
    PausableUpgradeable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable,
    VanaEpochStorageV1
{
    using EnumerableSet for EnumerableSet.UintSet;
    using EnumerableSet for EnumerableSet.AddressSet;

    bytes32 public constant MAINTAINER_ROLE = keccak256("MAINTAINER_ROLE");
    bytes32 public constant DLP_PERFORMANCE_ROLE = keccak256("DLP_PERFORMANCE_ROLE");

    event EpochCreated(uint256 epochId, uint256 startBlock, uint256 endBlock, uint256 rewardAmount);
    event EpochSizeUpdated(uint256 newEpochSize);
    event EpochRewardAmountUpdated(uint256 newEpochRewardAmount);
    event EpochDlpRewardAdded(uint256 epochId, uint256 dlpId, uint256 rewardAmount);
    event EpochFinalized(uint256 epochId);

    error EpochNotEnded();
    error EpochAlreadyFinalized();
    error InvalidEpoch();
    error EpochRewardExceeded();
    error EpochRewardNotDistributed();

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    struct InitializeParams {
        address ownerAddress;
        address dlpRegistryAddress;
        uint256 daySize;
        uint256 epochSize;
        uint256 epochRewardAmount;
    }

    function initialize(
        InitializeParams memory params
    ) external initializer {
        __AccessControl_init();
        __UUPSUpgradeable_init();
        __ReentrancyGuard_init();
        __Pausable_init();

        dlpRegistry = IDLPRegistry     (params.dlpRegistryAddress);
        daySize = params.daySize;
        epochSize = params.epochSize;
        epochRewardAmount = params.epochRewardAmount;

        _grantRole(DEFAULT_ADMIN_ROLE, params.ownerAddress);
        _grantRole(MAINTAINER_ROLE, params.ownerAddress);
    }

    function _authorizeUpgrade(address newImplementation) internal virtual override onlyRole(DEFAULT_ADMIN_ROLE) {}

    function version() external pure virtual override returns (uint256) {
        return 1;
    }

    function epochs(uint256 epochId) external view override returns (EpochInfo memory) {
        return
            EpochInfo({
                startBlock: _epochs[epochId].startBlock,
                endBlock: _epochs[epochId].endBlock,
                rewardAmount: _epochs[epochId].rewardAmount,
                isFinalized: _epochs[epochId].isFinalized
            });
    }

    function epochDlpIds(uint256 epochId) external view override returns (uint256[] memory) {
        return _epochs[epochId].dlpIds.values();
    }

    function epochDlps(uint256 epochId, uint256 dlpId) external view override returns (EpochDlpInfo memory) {
        Epoch storage epoch = _epochs[epochId];
        EpochDlp memory epochDlp = epoch.dlps[dlpId];

        return
            EpochDlpInfo({
                isTopDlp: epoch.dlpIds.contains(dlpId),
                rewardAmount: epochDlp.rewardAmount
            });
    }

    function pause() external override onlyRole(MAINTAINER_ROLE) {
        _pause();
    }

    function unpause() external override onlyRole(MAINTAINER_ROLE) {
        _unpause();
    }

    function updateEpochSize(uint256 newEpochSizeInDays) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        epochSize = newEpochSizeInDays;
        emit EpochSizeUpdated(newEpochSizeInDays);
    }

    function updateEpochRewardAmount(uint256 newEpochRewardAmount) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        epochRewardAmount = newEpochRewardAmount;
        emit EpochRewardAmountUpdated(newEpochRewardAmount);
    }

    function updateDlpRegistry(address dlpRegistryAddress) external override onlyRole(MAINTAINER_ROLE) {
        dlpRegistry = IDLPRegistry(dlpRegistryAddress);
    }

    function updateDlpPerformance(address dlpPerformanceAddress) external override onlyRole(MAINTAINER_ROLE) {
        _revokeRole(DLP_PERFORMANCE_ROLE, address(dlpPerformance));
        dlpPerformance = IDLPPerformance(dlpPerformanceAddress);
        _grantRole(DLP_PERFORMANCE_ROLE, address(dlpPerformance));
    }

    /**
     * @notice Creates epochs up to current block
     */
    function createEpochs() external override nonReentrant whenNotPaused {
        _createEpochsUntilBlockNumber(block.number);
    }

    /**
     * @notice Creates epochs up to specified block
     */
    function createEpochsUntilBlockNumber(uint256 blockNumber) external override nonReentrant whenNotPaused {
        _createEpochsUntilBlockNumber(blockNumber < block.number ? blockNumber : block.number);
    }

    function saveEpochDlpRewards(
        uint256 epochId,
        Rewards[] calldata dlpRewards,
        bool finalScores
    ) external override nonReentrant whenNotPaused onlyRole(DLP_PERFORMANCE_ROLE) {
        if (epochId > epochsCount) {
            revert InvalidEpoch();
        } else if (epochId == epochsCount && finalScores) {
            revert EpochNotEnded();
        }

        Epoch storage epoch = _epochs[epochId];

        if (epoch.isFinalized) {
            revert EpochAlreadyFinalized();
        }

        for (uint256 i = 0; i < dlpRewards.length; i++) {
            uint256 dlpId = dlpRewards[i].dlpId;
            uint256 rewardAmount = dlpRewards[i].rewardAmount;

            if (rewardAmount > 0) {
                epoch.dlpIds.add(dlpId);
            } else {
                epoch.dlpIds.remove(dlpId);
            }

            epoch.dlps[dlpId].rewardAmount = rewardAmount;

            emit EpochDlpRewardAdded(epochId, dlpId, rewardAmount);
        }

        uint256 totalRewardAmount;
        for (uint256 i = 0; i < epoch.dlpIds.length(); i++) {
            totalRewardAmount += epoch.dlps[epoch.dlpIds.at(i)].rewardAmount;

            if (totalRewardAmount > epoch.rewardAmount) {
                revert EpochRewardExceeded();
            }
        }

        if (finalScores) {
            if (totalRewardAmount < epoch.rewardAmount - 1e9) { //1e9 represents calculation error tolerance
                revert EpochRewardNotDistributed();
            }
            epoch.isFinalized = true;

            emit EpochFinalized(epochId);
        }
    }

    function forceFinalizedEpoch(uint256 epochId) external override nonReentrant whenNotPaused onlyRole(MAINTAINER_ROLE) {
        _createEpochsUntilBlockNumber(block.number);

        if (epochId >= epochsCount) {
            revert EpochNotEnded();
        }

        Epoch storage epoch = _epochs[epochId];

        epoch.isFinalized = true;

        emit EpochFinalized(epochId);
    }

    function initializeEpoch(
        uint256 epochId,
        uint256 startBlock,
        uint256 endBlock,
        uint256 rewardAmount,
        Rewards[] calldata dlpRewards,
        bool isFinalized
    ) public onlyRole(MAINTAINER_ROLE) {
        Epoch storage epoch = _epochs[epochId];

        epoch.startBlock = startBlock;
        epoch.endBlock = endBlock;
        epoch.rewardAmount = rewardAmount;
        epoch.isFinalized = isFinalized;

        for (uint256 i = 0; i < dlpRewards.length; i++) {
            uint256 dlpId = dlpRewards[i].dlpId;

            epoch.dlpIds.add(dlpId);
            epoch.dlps[dlpId].rewardAmount = dlpRewards[i].rewardAmount;
        }
    }

    /**
     * @notice Creates epochs up to target block
     */
    function _createEpochsUntilBlockNumber(uint256 blockNumber) internal {
        Epoch storage lastEpoch = _epochs[epochsCount];

        if (lastEpoch.endBlock > block.number) {
            return;
        }

        while (lastEpoch.endBlock < blockNumber) {
            Epoch storage newEpoch = _epochs[++epochsCount];
            newEpoch.startBlock = lastEpoch.endBlock + 1;
            newEpoch.endBlock = newEpoch.startBlock + epochSize * daySize - 1;
            newEpoch.rewardAmount = epochRewardAmount;

            emit EpochCreated(epochsCount, newEpoch.startBlock, newEpoch.endBlock, newEpoch.rewardAmount);
            lastEpoch = newEpoch;
        }
    }

//    function migrateEpochData(address dlpRootEpochAddress, uint256 epochIdStart, uint256 epochIdEnd) external onlyRole(MAINTAINER_ROLE) {
